import './Education.css'

import React, { useContext } from 'react';

import EducationCard from './EducationCard';
import { ThemeContext } from '../../contexts/ThemeContext';
import { educationData } from '../../data/educationData'

function Education() {

    const { theme } = useContext(ThemeContext);
    return (
        <div className="education" id="education" style={{backgroundColor: theme.secondary}}>
           
            <div className="education-body">
                <div className="education-description">
                <h1 style={{color:theme.primary}}>Educación/Formación</h1>
                    {educationData.map(edu => (
                        <EducationCard 
                            key={edu.id}
                            id={edu.id}
                            institution={edu.institution}
                            course={edu.course}
                            startYear={edu.startYear}
                            endYear={edu.endYear}
                        />
                    ))}
                </div>

                {/* <div className="education-image">
                    <img src={theme.eduimg} alt=""/>
                </div> */}

<div class="row">
  <div class="example-2 card">
    <div class="wrapper">
      <div class="header">
    
        <ul class="menu-content">
          <li>
            <a href="#" class="fa fa-bookmark-o"></a>
          </li>
          <li><a href="#" class="fa fa-heart-o"><span>18</span></a></li>
          <li><a href="#" class="fa fa-comment-o"><span>3</span></a></li>
        </ul>
      </div>
      <div class="data">
        <div class="content">
          {/* <span class="author">Jane Doe</span> */}
          <h1 class="title"><a href="#">Equipo de Profesores IT</a></h1>
          <p class="text">The antsy bingers of Netflix will eagerly anticipate the digital release of the Survive soundtrack, out today.</p>
          {/* <a href="#" class="button">Read more</a> */}
        </div>
      </div>
    </div>
  </div>
</div>







                
            </div>
        </div>
    )
}

export default Education
